using System;using Microsoft.Xna.Framework;using Terraria;using Terraria.ID;using Terraria.ModLoader;namespace TheNextWeapons.NPCs{    [AutoloadHead]    public class Rogue: ModNPC    {        public override string Texture => "TheNextWeapons/NPCs/Rogue";        public override bool Autoload(ref string name)        {            name = "Traveler";            return mod.Properties.Autoload;        }        public override void SetDefaults()        {            npc.townNPC = true;            npc.friendly = true;            npc.width = 18;            npc.height = 46;            npc.aiStyle = 7;            npc.defense = 25;            npc.lifeMax = 250;            npc.HitSound = SoundID.NPCHit1;            npc.DeathSound = SoundID.NPCDeath1;            npc.knockBackResist = 0.5f;            Main.npcFrameCount[npc.type] = 26;            NPCID.Sets.ExtraFramesCount[npc.type] = 0;            NPCID.Sets.AttackFrameCount[npc.type] = 0;            NPCID.Sets.DangerDetectRange[npc.type] = 150;	    NPCID.Sets.HatOffsetY[npc.type] = 4;            animationType = NPCID.Guide;	}        public override string TownNPCName()        {            switch (WorldGen.genRand.Next(1))            {                case 0:                    return "Robtops";                default:                    return "Bob";            }        }        public override void SetChatButtons(ref string button, ref string button2)        {            button = "Shop";   //this defines the buy button name        }        public override void OnChatButtonClicked(bool firstButton, ref bool openShop)        {            if (firstButton)            {                openShop = true;        }        }        public override void SetupShop(Chest shop, ref int nextSlot)       //Allows you to add items to this town NPC's shop. Add an item by setting the defaults of shop.item[nextSlot] then incrementing nextSlot.        {            {                shop.item[nextSlot].SetDefaults(ItemID.BladedGlove);                nextSlot++;            shop.item[nextSlot].SetDefaults(mod.ItemType("Tola"));            nextSlot++;            shop.item[nextSlot].SetDefaults(mod.ItemType("tshort"));            nextSlot++;            shop.item[nextSlot].SetDefaults(mod.ItemType("Tolon"));            nextSlot++;            shop.item[nextSlot].SetDefaults(mod.ItemType("Toot"));            nextSlot++;            shop.item[nextSlot].SetDefaults(mod.ItemType("beer"));            nextSlot++;            }            if (NPC.downedBoss3)   //this make so when Skeletron is killed the town npc will sell this            {            shop.item[nextSlot].SetDefaults(mod.ItemType("Tgun"));                nextSlot++;            }            if (NPC.downedMechBossAny)   //this make so when Skeletron is killed the town npc will sell this            {	shop.item[nextSlot].SetDefaults(ItemID.Ichor);	nextSlot++;	shop.item[nextSlot].SetDefaults(ItemID.CursedFlame);	nextSlot++;	shop.item[nextSlot].SetDefaults(ItemID.SoulofLight);	nextSlot++;	shop.item[nextSlot].SetDefaults(ItemID.SoulofNight);	nextSlot++;            }        }        public override string GetChat()       //Allows you to give this town NPC a chat message when a player talks to it.        {            switch (Main.rand.Next(4))    //this are the messages when you talk to the npc            {                case 0:                    return "Are you intrested in my equipment?";                case 1:                    return "Wanna Buy Somenthing?";                case 2:                    return "My prices are so low compared to others... i think.";                case 3:                    return "I heard that the ¨Rune Wizard¨ has a intresting dagger...";                default:                    return "I hate ¨Tim¨ he stole my dagger...";		}          }        public override float SpawnChance(NPCSpawnInfo spawnInfo)        {            return SpawnCondition.OverworldDay.Chance * 0.2f;        	}        public override bool CanTownNPCSpawn(int numTownNPCs, int money) 		{            for (int k = 0; k < 255; k++) 		{                Player player = Main.player[k];                if (!player.active) 		{                    continue;                }            }            return false;        }        public override void TownNPCAttackStrength(ref int damage, ref float knockback) {            damage = 60; // The amount of damage the Town NPC inflicts.            knockback = 8f; // The amount of knockback the Town NPC inflicts.        }        public override void TownNPCAttackCooldown(ref int cooldown, ref int randExtraCooldown) {            cooldown = 30; // The amount of ticks the Town NPC takes to cool down. Every 60 in-game ticks is a second.            randExtraCooldown = 30; // How long it takes until the NPC attacks again, but with a chance.        }        public override void TownNPCAttackProj(ref int projType, ref int attackDelay) {            projType = 30; // The Projectile this NPC shoots. Search up Terraria Projectile IDs, I cannot link the websites in this code            attackDelay = 1; // Delays the attacks, obviously.        }        public override void TownNPCAttackProjSpeed(ref float multiplier, ref float gravityCorrection, ref float randomOffset) {            multiplier = 5.5f; // The Speed of the Projectile            randomOffset = 2f; // Random Offset        }    }}